    private void pushDataLookup() {
        FirebaseDatabase database = FirebaseDatabase.getInstance();
        DatabaseReference reference = database.getReference("lookups");
        List<List<List<String>>> rooms = new ArrayList<>();
        for (int i = 0; i < 6; i++) {
            List<List<String>> screenings = new ArrayList<>();
            for (int j = 0; j < 6; j++) {
                List<String> seats = Collections.nCopies(16, "available");
                screenings.add(seats);
            }
            rooms.add(screenings);
        }
        reference.setValue(rooms);
    }

